@Article{sereno2009size,
  author = {Sara C. Sereno and Patrick J. O'Donnell and Margaret E. Sereno},
  title = {Short article: Size matters: Bigger is faster},
  journal = {Quarterly Journal of Experimental Psychology},
  volume = {62},
  number = {6},
  pages = {1115--1122},
  year = {2009},
  doi = {10.1080/17470210802618900},
  note = {PMID: 19142830},
  url = { 
        https://doi.org/10.1080/17470210802618900

},
  eprint = { 
        https://doi.org/10.1080/17470210802618900

},
  abstract = { A largely unexplored aspect of lexical access in visual word recognition is “semantic size”—namely, the real-world size of an object to which a word refers. A total of 42 participants performed a lexical decision task on concrete nouns denoting either big or small objects (e.g., bookcase or teaspoon). Items were matched pairwise on relevant lexical dimensions. Participants’ reaction times were reliably faster to semantically “big” versus “small” words. The results are discussed in terms of possible mechanisms, including more active representations for “big” words, due to the ecological importance attributed to large objects in the environment and the relative speed of neural responses to large objects. },
}

